import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate, useParams } from 'react-router-dom';
import Navbar from '../components/Navbar';

function LearningPathPage() {
  const { user } = useAuth();
  const { pathId } = useParams();
  const navigate = useNavigate();

  // Sample paths and modules (replace with backend data later)
  const paths = {
    '1': {
      title: 'Becoming a Data Scientist with AI',
      modules: [
        { id: 1, title: 'Introduction to Data Science', activity: 'Quiz: What is data science?' },
        { id: 2, title: 'Data Cleaning Basics', activity: 'Prompt: Clean this dataset...' },
        { id: 3, title: 'Building Simple Models', activity: 'Code: Predict outcomes...' },
      ],
    },
    '2': {
      title: 'AI Governance and Data Quality Management',
      modules: [
        { id: 1, title: 'Ethics in AI', activity: 'Quiz: Ethical dilemmas' },
        { id: 2, title: 'Data Privacy Laws', activity: 'Prompt: Summarize GDPR...' },
        { id: 3, title: 'Quality Checks', activity: 'Code: Validate data...' },
      ],
    },
    '3': {
      title: 'Using AI for App Development and Automation',
      modules: [
        { id: 1, title: 'AI in Coding', activity: 'Quiz: What is prompt engineering?' },
        { id: 2, title: 'Automation Tools', activity: 'Prompt: Automate a task...' },
        { id: 3, title: 'Building Prototypes', activity: 'Code: Generate app code...' },
      ],
    },
  };

  const path = paths[pathId] || { title: 'Unknown Path', modules: [] };

  const [completedModules, setCompletedModules] = useState([]); // Track completed modules
  const [points, setPoints] = useState(0); // Simulated points

  if (!user) {
    navigate('/'); // Redirect to login if not authenticated
    return null;
  }

  const completeModule = (moduleId) => {
    if (!completedModules.includes(moduleId)) {
      setCompletedModules([...completedModules, moduleId]);
      setPoints(points + 100); // Award 100 points per module
      // TODO: Update backend progress
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar />
      <div className="container mx-auto p-6">
        <h1 className="text-2xl font-bold mb-6">{path.title}</h1>
        <p className="text-lg mb-4">Points Earned: {points}</p>
        <div className="space-y-6">
          {path.modules.map((module, index) => {
            const isUnlocked = index === 0 || completedModules.includes(path.modules[index - 1].id);
            return (
              <div key={module.id} className="bg-white p-4 rounded-lg shadow-md">
                <h2 className="text-md font-medium mb-2">{module.title}</h2>
                <p className="text-sm text-gray-600 mb-4">Activity: {module.activity}</p>
                {isUnlocked ? (
                  completedModules.includes(module.id) ? (
                    <p className="text-accent font-semibold">Completed!</p>
                  ) : (
                    <button
                      onClick={() => completeModule(module.id)}
                      className="bg-primary text-white px-4 py-2 rounded-md hover:bg-opacity-90 transition duration-200"
                    >
                      Complete Module
                    </button>
                  )
                ) : (
                  <p className="text-gray-500">Locked - Complete previous module</p>
                )}
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default LearningPathPage;
